
public class two_eighteen {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 1, b = 2;
		
		System.out.println("a" + " b" + " pow(a, b)");
		
		System.out.print(a);
		System.out.print(" " + b);
		System.out.println(" " + (int)Math.pow(a, b));
		
		System.out.print(++a);
		System.out.print(" " + ++b);
		System.out.println(" " + (int)Math.pow(a, b));
		
		System.out.print(++a);
		System.out.print(" " + ++b);
		System.out.println(" " + (int)Math.pow(a, b));
		
		System.out.print(++a);
		System.out.print(" " + ++b);
		System.out.println(" " + (int)Math.pow(a, b));
		
		System.out.print(++a);
		System.out.print(" " + ++b);
		System.out.println(" " + (int)Math.pow(a, b));
	}

}
